describe('Import tests', () => {
  it('importing the basic logger works', async () => {
    const importedLogger = await import('../index');
    expect(importedLogger?.default?.info).not.toBe(null);
  });

  it('importing the fastify logger works', async () => {
    const importedLogger = await import('../index');
    expect(importedLogger?.fastifyLogger).not.toBe(null);
  });
});
