import { createLogger, LogLevel } from './logger';
import { createFastifyLogger } from './fastify';

const logger = createLogger({
  level: (process.env.LOGGING_LEVEL as LogLevel) || 'info',
  disableRedaction: process.env.LOGGING_DISABLE_REDACTION === 'true',
  prettyPrint: process.env.LOGGING_PRETTY === 'true',
});

export const fastifyLogger = createFastifyLogger({
  level: (process.env.LOGGING_LEVEL_HTTP as LogLevel) || 'info',
  prettyPrint: process.env.LOGGING_PRETTY === 'true',
});

export default logger;
