import React, { useState } from "react";
import styled from "styled-components";
import logo from "./images/logo.png";
import { ButtonSecondary } from "../components/Buttons";
const Body = styled.body`
  background: linear-gradient(to right, #a5bfb2, #fff);
`;

const LandingPageContainer = styled.div`
  font-family: Arial, Helvetica, sans-serif;
  max-width: 800px;
  margin: 0 auto;
`;

const Form = styled.form`
  max-width: 500px;
  margin: 0 auto;
  padding: 1rem;
  background-color: #fff;
  border-radius: 0.25rem;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.5);
  font-weight: bold;
`;

const Input = styled.input`
  display: block;
  width: 100%;
  padding: 0.3rem;
  font-size: 1rem;
  border-radius: 0.25rem;
  margin-bottom: 1rem;
  box-sizing: border-box;
`;

const Select = styled.select`
  display: block;
  width: 100%;
  padding: 0.3rem;
  font-size: 1rem;
  border-radius: 0.25rem;
  margin-bottom: 1rem;
  box-sizing: border-box;
`;

const H1 = styled.h1`
  text-align: center;
  font-size: 30px;
  font-weight: bolder;
  margin: 40px;
  text-transform: uppercase;
  color: rgb(65, 65, 65);
`;

const Label = styled.label`
  display: block;
  font-size: small;
  margin-bottom: 10px;
`;

const SubmitButton = styled.button`
  display: block;
  padding: 0.5rem 0.5rem;
  font-size: 1rem;
  background-color: #a5bfb2;
  color: #000000;
  border: none;
  border-radius: 0.25rem;
  cursor: pointer;
  font-weight: bold;
  margin-top: 10px;
`;

const Logo = styled.img`
  margin-bottom: 40px;
`;

const LandingPage: React.FC = () => {
  const [birthYear, setBirthYear] = useState<string>("");
  const [gender, setGender] = useState<string>("");
  const [location, setLocation] = useState<string>("");
  const [newsLetter, setNewsLetter] = useState<string>("");
  const [aanwezigOpeningsfeest, setAanwezigOpeningsfeest] =
    useState<string>("");
  const [email, setEmail] = useState<string>("");

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
  };

  return (
    <Body>
      <LandingPageContainer>
        <Logo
          src={logo}
          alt="Logo"
          style={{ width: "200px", height: "100px" }}
        />
        {/* <H1>Registratie openingsfeest CultuurHoek</H1> */}
        <Form onSubmit={handleSubmit}>
          <Label htmlFor="aanwezig_openingsfeest">
            Aanwezig bij openingsfeest op 24 september
          </Label>
          <Select
            id="aanwezig_openingsfeest"
            name="aanwezig_openingsfeest"
            value={aanwezigOpeningsfeest}
            onChange={(e) => setAanwezigOpeningsfeest(e.target.value)}
          >
            <option value="">-- Maak een keuze --</option>
            <option value="Ja">Ja, ik ben aanwezig</option>
            <option value="Nee">Nee, ik kan helaas niet aanwezig zijn</option>
          </Select>

          <Label htmlFor="email">Email</Label>
          <Input
            id="email"
            type="email"
            name="email"
            placeholder="Vul hier je emailadres in"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <ButtonSecondary type="submit">Verstuur</ButtonSecondary>
        </Form>
      </LandingPageContainer>
    </Body>
  );
};
 export default Landing;
